import React from 'react'
import "./product.css"
import { Link } from 'react-router-dom'

export const Product = (props) => {

    const { product, showButton } = props

    return (

        <div className="card" style={{ maxHeight: 500 }}>
            <img src={product.image} className="card-img-top img-style" alt="..." />
            <div div className="card-body" >
                <h5 className="card-title">{product.title}.slice(0, 30)</h5>
                <p className="card-text">{product.description}</p>
                {
                    showButton &&
                    <Link className='btn btn-primary' to={`/product/${product.id}`}>Details</Link>
                }
            </div >
        </div >

    )
}

export default Product;
